import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const AllCategory = () => {

    const [categories, setCategories] = useState([]);

    useEffect(() => {
        AllCategoryList();
    }, [])

    const AllCategoryList = () => {
        axios.get("category/list")
            .then(resp => {
                console.log(resp.data);
                setCategories(resp.data);
            }).catch(err => {
                console.log(err);
            });
    }

   // Delete Category
    const deleteCategory = (id) => {
        axios.get(`category/delete/${id}`)
            .then(resp => {
                    if(resp.data === 'Deleted')
                        {
                            AllCategoryList();
                            console.log(resp.data);
                        }
            }).catch(err => {
                console.log(err);
            });
    }


    return (
        <div className="container">
            <h1>All Category</h1>

            <table id="tableDesign">
                <tr>
                    <th>Name</th>
                    <th colSpan={2}>Action</th>
                    {/* <th></th> */}
                </tr>

                {
                    categories.map(c => (
                        <tr>
                            <td>{c.name}</td>
                            <td><Link className="buttonDetails3" to={"/edit/category/" + c.id + "/" + c.name}><p>Edit</p></Link></td>
                            <td><button className="buttonDetails3" onClick={() =>deleteCategory(c.id)} ><p>Delete</p></button></td>   
                        </tr>
                    ))
                }
            </table>
        </div>
    )


}
export default AllCategory;
